import React, { useState } from "react";
import TransactionsList from "./TransactionsList";
import Search from "./Search";
import AddTransactionForm from "./AddTransactionForm";

function AccountContainer() {

  const [formData, setFormData]= useState({
    date:"2019-12-01",
    description:"bought a watch",
    category:"Fashion",
    amount:"120",
  })

  function handleChange(event){
    // name is the KEY in the formData object we're trying to update

    const name = event.target.name;
    const value = event.target.value;

    setFormData({
      ...formData,
      [name]:value,
    })

  }

  function handleSubmit(event){
    event.preventDefault();
    const newFormData = {
      date:"date",
      description:"description",
      category:"category",
      amount:"amount",
    }
    const dataArray = [...formData, newFormData];
    setFormData(dataArray);
  }

  return (
    <div>
      <Search />
      <AddTransactionForm handleSubmit={handleSubmit} formData={formData} handleChange={handleChange}/>
      <TransactionsList />
    </div>
  );
}

export default AccountContainer;
