import React, { useState } from "react";
import TransactionsList from "./TransactionsList";
import Search from "./Search";
import AddTransactionForm from "./AddTransactionForm";

function AccountContainer({transactions, setTransactions}) {

  const [formData, setFormData]= useState({
    date:"",
    description:"",
    category:"",
    amount:"",
  })

  function handleChange(event){
    // name is the KEY in the formData object we're trying to update

    const name = event.target.name;
    const value = event.target.value;

    setFormData({
      ...formData,
      [name]:value,
    });

  }

  function handleSubmit(event){
    event.preventDefault();
    const newTransaction = {
      date: formData.date,
      description: formData.description,
      category: formData.category,
      amount: formData.amount,
    };
    // Add the new item to the items list
    setTransactions([...transactions, newTransaction]);
  }

  return (
    <div>
      <Search />
      <AddTransactionForm handleSubmit={handleSubmit} formData={formData} handleChange={handleChange}/>
      <TransactionsList />
    </div>
  );
}

export default AccountContainer;
